import { React } from "react";
import { Alert, Badge } from "react-bootstrap";

const Header = ({ personsLength }) => {
  let badgeStyle = null;

  if (personsLength <= 1) badgeStyle = "danger";
  else if (personsLength == 2) badgeStyle = "warning";
  else if (personsLength >= 3) badgeStyle = "success";

  return (
    <div>
      <Alert variant="primary">
        <h2>برنامه مدیریت اشخاص</h2>
      </Alert>

      <Alert variant="light">
        <h6>
          تعداد اشخاص ثبت شده{" "}
          <Badge variant={badgeStyle} pill className="pt-1">
            {personsLength}
          </Badge>{" "}
          نفر می باشد
        </h6>
      </Alert>
    </div>
  );
};

export default Header;
